              </thead>
              <tbody>
                {items.map((item, index) => (
                  <tr key={index}>
                    <td className="border border-gray-300 p-2">{item.seal_type}</td>
                    <td className="border border-gray-300 p-2">{item.material_type}</td>
                    <td className="border border-gray-300 p-2">
